#include <array> int FIFO(std::vector<int> &page_ref, int frame_size)
{ std::queue<int> page_frames; std::unordered_set<int> frames; int page_faults = 0; for (int page : page_ref) { if (frames.find(page) == frames.end()) { page_faults++; if (page_frames.size() == frame_size) { int front = page_frames.front(); page_frames.pop(); frames.erase(front); } page_frames.push(page); frames.insert(page); } } return page_faults;
} int LRU(std::vector<int> &page_ref, int frame_size)
{ std::vector<int> page_frames; int page_faults = 0; for (int i = 0; i < page_ref.size(); i++) { int page = page_ref[i]; for (int j = 0; j < page_frames.size(); j++) { if (page_frames[j] == page) { page_frames.push_back(page); page_frames.erase(page_frames.begin() + j); break; } } if (page_frames.size() == 0 || page_frames.size() < frame_size && page_frames.back() != page) { page_faults++; page_frames.push_back(page); continue; } if (page_frames.back() != page) { page_faults++; page_frames.erase(page_frames.begin()); page_frames.push_back(page); } } return page_faults;
}
int OPT(std::vector<int> &page_ref, int frame_size)
{ std::vector<int> page_frames; int page_faults = 0; for (int i = 0; i < page_ref.size(); i++) { int page = page_ref[i]; auto p = std::find(page_frames.begin(), page_frames.end(), page); if (p == page_frames.end()) { page_faults++; if (page_frames.size() < frame_size) { page_frames.push_back(page); continue; } int index = 0; int max = page_frames.size(); for (int j = 0; j < page_frames.size(); j++) { auto f = std::find(page_ref.begin() + i, page_ref.end(), page_frames[j]); if (f - page_ref.begin() > max) { max = f - page_ref.begin(); index = j; } } page_frames[index] = page; } } return page_faults;
