int SCAN(std::vector<int> &req, int head)
{ int dis = 0; std::sort(req.begin(), req.end()); auto it = std::lower_bound(req.begin(), req.end(), head); std::vector<int> left(req.begin(), it); std::vector<int> right(it, req.end()); for (auto it = right.begin(); it != right.end(); ++it) { dis += abs(*it - head); head = *it; } if (!left.empty()) { dis += abs(MAX_DISK - head); head = MAX_DISK; for (auto it = left.rbegin(); it != left.rend(); ++it) { dis += abs(*it - head); head = *it; } } return dis;
}
int C_SCAN(std::vector<int> &req, int head)
{ int dis = 0; std::sort(req.begin(), req.end()); auto it = std::lower_bound(req.begin(), req.end(), head); std::vector<int> left(req.begin(), it); std::vector<int> right(it, req.end()); for (auto it = right.begin(); it != right.end(); ++it) { dis += abs(*it - head); head = *it; } if (!left.empty()) { dis += abs(MAX_DISK - head); dis += MAX_DISK; head = 0; for (auto it = left.begin(); it != left.end(); ++it) { dis += abs(*it - head); head = *it; } } return dis;
